import { action, observable } from '@fe/utils/decorator'
import Root from '@fe/utils/models/Root'

export default class State extends Root {
  @observable tree = {}
  // @observable active

  @action
  async fetchTree() {
    const { data, code } = await fetch('/api/fileView/get')
    if (code === 200) {
      this.tree = data
      // this.active = data.active
    }
  }

  @action init(/*props*/) {
    this.fetchTree()
  }

  @action update(/*props*/) {
  }

  @action exit() {
  }
}