import React from 'react'
import { observer } from 'mobx-react'
import FileTree from '@fe/comps/FileTree'
import sio from '@fe/utils/hoc/state-in-out'
import { debounce } from 'lodash'

import State from './State'

@sio(State, 'local')
@observer
export default class Home extends React.Component {
  constructor(props) {
    super(props)
    this.saveTree = debounce(this.saveTree, 2000, { trailing: true })
  }

  async saveTree(tree) {
    const { data, code } = await fetch('/api/fileView/set', {
      method: 'post', body: tree
    })
    if (code === 200) {
      this.tree = data
    }
  }

  render() {
    return (
      <div className="page-home">
        <div className="flex-container">
          <div>
            <FileTree
              tree={this.local.tree}
              onChange={tree => this.saveTree(tree)}
            />
          </div>
          <div>

          </div>
        </div>
      </div>
    )
  }
}
