import React from 'react'
import { observer } from 'mobx-react'
import urlsync from '@fe/utils/decorator/url-sync/react'
import { Link } from 'react-router'

@observer
export default class Home extends React.Component {

  @urlsync(null, { initialWrite: true })
  count = 1

  render() {

    return (
      <div className="page-home">
        <h1 className="title">
          Home
          <input value={this.count} onChange={evt => this.count = evt.target.value}/>
        </h1>
        <Link to={'/edit'}>Edit</Link>
      </div>
    )
  }
}
