import {
  invokedWithArgs
} from '../utils'

import urlsync from './core'

const keyMap = {
  initKey: 'init',
  updateKey: 'update',
  exitKey: 'exit'
}

export default (...args) => {
  if (invokedWithArgs(args)) {
    // console.warn('`urlsync` 不需要传参')
    return (...innerArgs) =>
      urlsync(args[0] ? args[0] : innerArgs[1], { ...keyMap, ...args[1] }, ...innerArgs)
  } 
  return urlsync(args[1], keyMap, ...args)
    
}